mapper.select:
SELECT a.id, a.biz_type AS bizType, a.biz_id AS bizId, a.trigger_source AS triggerSource, a.audit_type AS auditType, a.audit_status AS auditStatus, a.created_at AS createdAt, a.model_label AS modelLabel, a.model_score AS modelScore, a.hit_detail AS hitDetail, a.submit_user_id AS submitUserId, su.nickname AS submitUserName, a.auditor_id AS auditorId, a.audited_at AS auditedAt, a.reject_reason AS rejectReason, COALESCE(q.title, kb.title, CONCAT('Answer#', ans.id), CONCAT('Comment#', c.id)) AS contentTitle, LEFT(COALESCE(q.content, ans.content, c.content, kb.summary, kb.content), 200) AS contentSnippet, COALESCE(q.status, ans.status, c.status, kb.status) AS contentStatus FROM cms_audit a LEFT JOIN qa_question q ON a.biz_type = 1 AND q.id = a.biz_id LEFT JOIN qa_answer ans ON a.biz_type = 2 AND ans.id = a.biz_id LEFT JOIN qa_comment c ON a.biz_type = 3 AND c.id = a.biz_id LEFT JOIN kb_entry kb ON a.biz_type = 4 AND kb.id = a.biz_id LEFT JOIN `user` su ON su.id = a.submit_user_id WHERE 1 = 1
AND a.biz_type = #{bizType}
AND a.audit_status = #{auditStatus}
AND a.trigger_source = #{triggerSource}
AND ( q.title LIKE CONCAT('%', #{keyword}, '%') OR q.content LIKE CONCAT('%', #{keyword}, '%') OR ans.content LIKE CONCAT('%', #{keyword}, '%') OR c.content LIKE CONCAT('%', #{keyword}, '%') OR kb.title LIKE CONCAT('%', #{keyword}, '%') OR kb.summary LIKE CONCAT('%', #{keyword}, '%') OR kb.content LIKE CONCAT('%', #{keyword}, '%') )
AND a.created_at >= #{startTime}
AND a.created_at <= #{endTime}
ORDER BY
a.model_score a.created_at
ASC DESC , a.id DESC
