mapper.select:
SELECT r.id, r.biz_type AS bizType, r.biz_id AS bizId, r.reason_type AS reasonType, r.reason_code AS reasonCode, r.reason_detail AS reasonDetail, r.reporter_id AS reporterId, r.created_at AS createdAt, r.status, r.handler_id AS handlerId, r.handled_at AS handledAt, r.handle_action AS handleAction, r.handle_result AS handleResult, COALESCE(q.title, kb.title, CONCAT('Answer#', ans.id), CONCAT('Comment#', c.id)) AS contentTitle, LEFT(COALESCE(q.content, ans.content, c.content, kb.summary, kb.content), 200) AS contentSnippet, COALESCE(q.status, ans.status, c.status, kb.status) AS contentStatus, u.id AS authorId, u.nickname AS authorName FROM cms_report r LEFT JOIN qa_question q ON r.biz_type = 1 AND q.id = r.biz_id LEFT JOIN qa_answer ans ON r.biz_type = 2 AND ans.id = r.biz_id LEFT JOIN qa_comment c ON r.biz_type = 3 AND c.id = r.biz_id LEFT JOIN kb_entry kb ON r.biz_type = 4 AND kb.id = r.biz_id LEFT JOIN `user` u ON u.id = COALESCE(q.user_id, ans.user_id, c.user_id, kb.author_user_id) WHERE 1 = 1
AND r.biz_type = #{bizType}
AND r.status = #{status}
AND r.reason_type = #{reasonType}
AND ( COALESCE(q.title, kb.title, '') LIKE CONCAT('%', #{keyword}, '%') OR r.reason_detail LIKE CONCAT('%', #{keyword}, '%') OR r.reason_code LIKE CONCAT('%', #{keyword}, '%') )
AND r.created_at >= #{startTime}
AND r.created_at <= #{endTime}
ORDER BY r.created_at DESC, r.id DESC
