mapper.select:
SELECT e.id, e.title, e.category_id AS categoryId, c.name AS categoryName, e.status, e.created_at AS createdAt, e.updated_at AS updatedAt, e.view_count AS viewCount, e.like_count AS likeCount, e.favorite_count AS favoriteCount FROM kb_entry e LEFT JOIN kb_category c ON c.id = e.category_id WHERE 1 = 1
AND e.title LIKE CONCAT('%', #{keyword}, '%')
AND e.status = #{status}
AND e.category_id = #{categoryId}
AND EXISTS ( SELECT 1 FROM kb_entry_tag ket WHERE ket.entry_id = e.id AND ket.tag_id = #{tagId} )
AND e.created_at >= #{startTime}
AND e.created_at <= #{endTime}
ORDER BY
e.view_count e.like_count e.favorite_count e.created_at
ASC DESC , e.id DESC
