mapper.select:
WITH RECURSIVE category_tree AS ( SELECT c.id AS ancestor_id, c.id AS descendant_id FROM qa_category c WHERE c.delete_flag = 0 UNION ALL SELECT ct.ancestor_id, child.id AS descendant_id FROM category_tree ct JOIN qa_category child ON child.parent_id = ct.descendant_id AND child.delete_flag = 0 ), category_question_count AS ( SELECT ct.ancestor_id AS category_id, COUNT(q.id) AS question_count FROM category_tree ct LEFT JOIN qa_question q ON q.category_id = ct.descendant_id AND q.delete_flag = 0 GROUP BY ct.ancestor_id ) SELECT c.id, c.name, c.parent_id AS parentId, c.icon, c.description, c.status, c.sort, c.created_at AS createdAt, c.updated_at AS updatedAt, COALESCE(cqc.question_count, 0) AS questionCount FROM qa_category c LEFT JOIN category_question_count cqc ON cqc.category_id = c.id WHERE c.delete_flag = 0
AND c.name LIKE CONCAT('%', #{name}, '%')
AND c.parent_id = #{parentId}
AND c.status = #{status}
ORDER BY c.sort ASC, c.created_at DESC

SELECT COUNT(1)
FROM qa_topic_category tc
WHERE tc.category_id = #{categoryId}
